if($(".tree > ul"))
{
	var mytreebranch=$(".tree").find("li:has(ul)").addClass("parent_li").attr("role","treeitem").find(" > span").attr("title","Cerrar esta rama");
	$(".tree > ul").attr("role","tree").find("ul").attr("role","group"),
	mytreebranch.on("click",function(a){
		var b=$(this).parent("li.parent_li").find(" > ul > li");

		b.is(":visible")?(b.hide("fast"),
				$(this).attr("title","Abrir esta rama").find(" > i").addClass("icon-plus-sign").removeClass("icon-minus-sign")):(b.show("fast"),
						$(this).attr("title","Cerrar esta rama").find(" > i").addClass("icon-minus-sign").removeClass("icon-plus-sign")),a.stopPropagation()
	})
}